#include <iostream>
#include <sstream>
#include <random>
#include "Stoutonian.h"
#include "Names.h"

// default constructor creates a Stoutonian with a randomly-generated type, name, and stats
Stoutonian::Stoutonian()
{
    spawn(randomType());
}

// default constructor creates a Stoutonian with the specified StoutonianType and a randomly-generated name and stats
Stoutonian::Stoutonian(StoutonianType type)
{
    spawn(type);
}

// full constructor creates a Stoutonian with the provided type, name, and stats
Stoutonian::Stoutonian(StoutonianType type, string name, int sharpness, int strength, int speed)
: m_Type(type), m_Name(name), m_InitialMentalSharpness(sharpness), m_ChallengeStrength(strength), m_Speed(speed)
{
    m_ActualMentalSharpness = m_InitialMentalSharpness;
}

Stoutonian::~Stoutonian()
{
    //dtor
}

StoutonianType Stoutonian::typeFromInt(int type)
{
    switch (type)
    {
    case 0:
        return GameDesigner;
    case 1:
        return MathGenius;
    case 2:
        return InfoTechGuru;
    default:
        return Unknown;
    }
}

StoutonianType Stoutonian::randomType()
{
    return static_cast<StoutonianType>(rand() % 4);
}

// creates a Stoutonian from the specified type; name and speed are randomly generated
void Stoutonian::spawn(StoutonianType type)
{
    Names *names = Names::getInstance();

    m_Type = type;
    m_Name = names->randomName();

    switch (type)
    {
    case GameDesigner:
        m_InitialMentalSharpness = 30;
        m_ChallengeStrength = 20;
        m_Speed = random(10, 20);
        break;
    case MathGenius:
        m_InitialMentalSharpness = 40;
        m_ChallengeStrength = 15;
        m_Speed = random(10, 20);
        break;
    case InfoTechGuru:
        m_InitialMentalSharpness = 30;
        m_ChallengeStrength = 15;
        m_Speed = random(15, 25);
        break;
    default: // in case we want the "Unknown" Stoutonian type
        m_InitialMentalSharpness = random(2, 4) * 10; // between 20 and 40
        m_ChallengeStrength = random(2, 5) * 5; // between 10 and 25
        m_Speed = random(10, 25);
    }

    m_ActualMentalSharpness = m_InitialMentalSharpness;
}

int Stoutonian::random(int low, int high)
{
    return rand() % high + low;
}

void Stoutonian::speak()
{
    switch (m_Type)
    {
    case GameDesigner:
        cout << "    \"" << m_Name << " here, the best Game Designer in the universe!\" " << randomAction() << endl;
        cout << "    \"I'm the smartest Stoutonian with my Mental Sharpnesss of " << m_InitialMentalSharpness << " and I can deal " << m_ChallengeStrength << " hit points with ease.\"";
        break;
    case MathGenius:
        cout << "    \"Cower before the great Math Genius " << m_Name << "!\" " << randomAction() << endl;
        cout << "    \"I'm the sharpest of the Stoutonians with an MS of " << m_InitialMentalSharpness << " and a maximum " << m_ChallengeStrength << " hit point capability.\"";
        break;
    case InfoTechGuru:
        cout << "    \"I am " << m_Name << ", the IT Guru. No Stoutonian is better than me.\" " << randomAction() << endl;
        cout << "    \"My " << m_InitialMentalSharpness << " Mental Sharpness gives me " << m_ChallengeStrength << " hit points.\"";
        break;
    default:
        cout << "    \"This is " << m_Name << ", nerd extraordinaire.\" " << randomAction() << endl;
        cout << "    \"I have " << m_InitialMentalSharpness << " units of Mental Sharpness and " << m_ChallengeStrength << " maximum hit points I can deal.\"";
        break;
    }
}

string Stoutonian::stats()
{
    ostringstream ss;
    ss << m_Name << "\t" << getTypeName(m_Type) << "\t    " << m_ActualMentalSharpness << "\t\t" << m_ChallengeStrength;
    return ss.str();
}

