string Stoutonian::getTypeName(StoutonianType type)
{
    switch (type)
    {
    case GameDesigner:
        return "Game Designer";
    case MathGenius:
        return "Math Genius";
    case InfoTechGuru:
        return "Info Tech Guru";
    default:
        return "Unknown";
    }
}

void Stoutonian::rest()
{
    cout << m_Name << " is resting..." << endl;
    m_ActualMentalSharpness = m_InitialMentalSharpness;
}

bool Stoutonian::runAway()
{
    // generates 0, 1, 2, or 3; 0 is the bottom 25% so returning > 0 to provide 75% runAway success
    return rand() % 4 > 0;
}

